mapper.update:
UPDATE PRODUCT
SET
    NAME = #{name},
    DETAIL = #{detail},
    CATEGORY = #{category},
    SALES_LOCATION = #{salesLocation}
WHERE
    NO = #{productNo}
  AND USER_NO = #{userNo}

UPDATE NORMAL
SET
    PRICE = #{price}
WHERE
    NO2 = #{productNo}

mapper.delete:
DELETE FROM IMAGES
WHERE
ENTITY_ID = #{productNo}
AND ENTITY_TYPE = 'PRODUCT'
AND IMAGE_URL IN

    #{url}

mapper.update:
UPDATE PRODUCT
SET STATUS = 'N'
WHERE NO = #{productNo}

mapper.select:
SELECT COUNT(*)
FROM WISHLIST
WHERE USER_NO = #{userNo}
  AND PRODUCT_NO = #{productNo}

mapper.delete:
DELETE FROM WISHLIST
WHERE USER_NO = #{userNo}
  AND PRODUCT_NO = #{productNo}

mapper.update:
UPDATE NORMAL
SET VIEWS = VIEWS + 1
WHERE NO2 = #{productNo}
