mapper.update:
UPDATE PRODUCT
SET
    NAME = #{name},
    DETAIL = #{detail},
    CATEGORY = #{category},
    SALES_LOCATION = #{salesLocation}
WHERE
    NO = #{productNo}
  AND USER_NO = #{userNo}

UPDATE NORMAL
SET
    PRICE = #{price}
WHERE
    NO2 = #{productNo}

mapper.delete:
DELETE FROM IMAGES
WHERE
ENTITY_ID = #{productNo}
AND ENTITY_TYPE = 'PRODUCT'
AND IMAGE_URL IN

    #{url}

mapper.update:
UPDATE PRODUCT
SET STATUS = 'N'
WHERE NO = #{productNo}

mapper.select:
SELECT COUNT(*)
FROM WISHLIST
WHERE USER_NO = #{userNo}
  AND PRODUCT_NO = #{productNo}

mapper.delete:
DELETE FROM WISHLIST
WHERE USER_NO = #{userNo}
  AND PRODUCT_NO = #{productNo}

mapper.update:
UPDATE NORMAL
SET VIEWS = VIEWS + 1
WHERE NO2 = #{productNo}

mapper.select:
SELECT DISTINCT P.NO AS PRODUCT_NO, P.NAME, NP.PRICE, NP.REGION, P.CREATE_DATE, P.sales_location as location, NP.VIEWS, (SELECT IM.IMAGE_URL FROM IMAGES IM WHERE IM.ENTITY_TYPE = 'PRODUCT' AND IM.ENTITY_ID = P.NO ORDER BY IM.IMAGE_ID ASC FETCH FIRST 1 ROW ONLY) AS THUMBNAIL_URL, (SELECT COUNT(*) FROM WISHLIST WHERE PRODUCT_NO = P.NO) AS WISHLIST_COUNT, CASE WHEN NP.NO IS NOT NULL THEN 'USED' ELSE 'AUCTION' END AS PRODUCT_TYPE, C.NAME AS CATEGORY_NAME, NP.STATE AS PRODUCT_STATE, P.STATUS, U.NICKNAME AS seller_nickname FROM PRODUCT P JOIN NORMAL NP ON P.NO = NP.NO2 JOIN IMAGES IMG ON ENTITY_ID = P.NO LEFT JOIN CATEGORY C ON P.CATEGORY = C.NO LEFT JOIN USERS U ON P.USER_NO = U.USER_NO WHERE P.STATUS = 'Y' AND NP.STATE = 'A'
AND P.CATEGORY = (SELECT NO FROM CATEGORY WHERE NAME = #{category})
AND P.SALES_LOCATION LIKE '%' || #{location} || '%'
AND COALESCE(TO_NUMBER(N.PRICE), TO_NUMBER(A.CURRENT_PRICE)) >= #{minPrice}
AND COALESCE(TO_NUMBER(N.PRICE), TO_NUMBER(A.CURRENT_PRICE)) <= #{maxPrice}
AND N.STATE != 'N'

mapper.update:
update product
set create_date = sysdate
where no = #{productNo}
